import 'package:chat_gpt_task/model/OnBoarding/on_boarding.dart';import 'package:chat_gpt_task/features/OnBoarding/widgets/list_of_text.dart';import 'package:chat_gpt_task/shared/style/color.dart';import 'package:chat_gpt_task/shared/style/enum/enum.dart';import 'package:flutter/material.dart';import 'package:flutter_screenutil/flutter_screenutil.dart';import 'package:flutter_svg/flutter_svg.dart';import 'package:shimmer/shimmer.dart';class OnBoardingItem extends StatefulWidget {  const OnBoardingItem({super.key, required this.onBoardingModel});  final OnBoardingModel onBoardingModel;  @override  State<OnBoardingItem> createState() => _OnBoardingItemState();}class _OnBoardingItemState extends State<OnBoardingItem> {  @override  Widget build(BuildContext context) {    return SingleChildScrollView(      child: Column(crossAxisAlignment: CrossAxisAlignment.center, children: [        SvgPicture.asset(          widget.onBoardingModel.image,          width: 24,          fit: BoxFit.fitHeight,          color: isDark ? AppMainColors.whiteColor : AppMainColors.darkColor,          height: 24.h,          placeholderBuilder: (_) => Shimmer.fromColors(            baseColor: isDark ? Colors.grey[850]! : Colors.grey[350]!,            highlightColor: isDark ? Colors.grey[800]! : Colors.grey[300]!,            child: Container(              height: 350.h,              color: isDark ? AppMainColors.secondColor : AppMainColors.greyColor.withOpacity(0.2),            ),          ),        ),        SizedBox(height: 12.h),        Text(          widget.onBoardingModel.title,          textAlign: TextAlign.center,          style: Theme.of(context).textTheme.titleMedium!.copyWith(                fontWeight: FontWeight.w600,            color: isDark ? AppMainColors.whiteColor : AppMainColors.darkColor,              ),        ),        SizedBox(height: 40.h),        ListOfText(onBoardingModel: widget.onBoardingModel.body),        SizedBox(height: 16.h),        ListOfText(onBoardingModel: widget.onBoardingModel.label),        SizedBox(height: 16.h),        ListOfText(onBoardingModel: widget.onBoardingModel.text),      ]),    );  }}